import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

const resources = {
  en: {
    translation: {
      logo: "Kirana Bazaar",
      login: "Login",
      cart: "Cart",
      search: "Search",
      microphone: "Search by voice"
    }
  },
  hi: {
    translation: {
      logo: "किराना बाजार",
      login: "लॉगिन",
      cart: "कार्ट",
      search: "खोजें",
      microphone: "वॉयस से खोजें"
    }
  },
  mr: {
    translation: {
      logo: "किराणा बाजार",
      login: "लॉगिन",
      cart: "कार्ट",
      search: "शोधा",
      microphone: "आवाज वापरून शोधा"
    }
  }
};

i18n.use(initReactI18next).init({
  resources,
  lng: "en", // default language
  interpolation: {
    escapeValue: false
  }
});

export default i18n;
