import React, { useState, useEffect, useRef } from 'react';
import "../styles/registrationguide.css";

const steps = [
  "Enter GST number",
  "Verify GST number",
  "Enter store name",
  "Enter pickup address",
  "Choose shipping method",
  "Add bank account",
  "List your products",
  "Launch your store",
];

const RegistrationGuide = () => {
  const [activeStep, setActiveStep] = useState(0);
  const sectionsRef = useRef([]);

  // Scroll observer to track which section is in view
  useEffect(() => {
    const handleScroll = () => {
      const offset = window.innerHeight * 0.3; // Adjust this for when the section should be marked
      const index = sectionsRef.current.findIndex((section) => {
        const rect = section.getBoundingClientRect();
        return rect.top >= 0 && rect.top <= offset;
      });
      if (index !== -1) {
        setActiveStep(index);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="registration-guide">
      {/* Intro Section */}
      <div className="intro-section">
        <p className="breadcrumb">Amazon Seller &gt; Sell Online &gt; Seller Registration Guide</p>
        <h1>Step-by-step registration guide for new sellers</h1>
        <p className="intro-description">
          Step-by-step walk-through of the seller registration process on Amazon.in.
          Create your seller account to launch your business on Amazon.in with ease and
          start selling to crores of customers today.
        </p>
        <button className="start-selling-btn">Start Selling</button>
        <img
          src={require("../../assets/images/seller-intro.png")}
          alt="Seller Registration Intro"
          className="intro-image"
        />
      </div>

      {/* Main Content Section */}
      <div className="guide-main">
        <div className="guide-sidebar">
          <h3>Start seller registration</h3>
          <ul className="step-list">
            {steps.map((step, index) => (
              <li
                key={index}
                className={`step-item ${index === activeStep ? "active" : ""}`}
              >
                {step}
              </li>
            ))}
          </ul>
        </div>
        <div className="guide-content">
          {steps.map((step, index) => (
            <section
              key={index}
              ref={(el) => (sectionsRef.current[index] = el)}
              className="content-section"
            >
              <h2>{step}</h2>
              <p>{`Details about ${step.toLowerCase()}.`}</p>
            </section>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RegistrationGuide;
