import React from 'react';
import { Col, Row } from 'reactstrap';
import "../styles/home.css"
import hero from '../../assets/images/hero-image1.png';
import category1 from '../../assets/images/category1.png';


const categories = [
  { id: 1, name: "Exotic Fruits", image: category1, link: "/categories/exotic-fruits" }
];

const Home = () => {
  return (
    <>
      {/* ============ hero section start =========== */}
      <section className='hero-section'>

          <Row>
            <Col lg='12'>
              <div className='hero-background'></div>
                <div className='hero-text'>
                  <h1>Welcome to KiranaBazaar</h1>
                  <p>Your one-stop solution for all grocery needs</p>
                </div>
                <a href="/selling" className="hero-image-link">
                <img
                  src={hero}
                  alt="Start Selling"
                  className="hero-image"
                />
              </a>
            </Col>
          </Row>

      </section>
      {/* ============ hero section end =========== */}

      {/* ============ Categories Section Start =========== */}
      <section className='categories-section'>
        <h2 className='section-title'>Explore Categories</h2>
        <Row className='categories-grid'>
          {categories.map((category) => (
    <Col lg='4' md='6' sm='12' key={category.id} className='category-item'>
      <a href={category.link} className='category-link'>
        <div className='category-card'>
          <img src={category.image} alt={category.name} className='category-image' />
          <p className='category-name'>{category.name}</p>
        </div>
      </a>
    </Col>
  ))}
</Row>

      </section>
      {/* ============ Categories Section End =========== */}

    </>
  );
};


export default Home;
